# data: 22/06/2021# autor: Diego Ferruzzo# ==============================================================================# As Séries de Fourier - Exemplo de sinal períodico dislocado# ==============================================================================clear allclose allclc# Os parâmetros do exemplo Ts/T0 = 4Ts = 1/2;T0 = 4;       # o o períodow0 = 2*pi/T0; # a frequência angular# A Série Trigonométricaa0 = 0; ak =@(k) (8./(k.*pi)).*sin(k.*pi./4); bk =@(k) 0.*k;# A Série Compactac0  = a0;                               # o coeficiente c0ck  =@(k) sqrt(ak(k).^2 + bk(k).^2);    # o coeficente ckthk =@(k) -atan2(bk(k), ak(k));         # o ângulo theta(k)# A Série ExponencialX0 = c0;Xk =@(k) (1/2).*(ak(k)-j.*bk(k));# Definimos um vetor tempot = -T0:0.01:T0;   # queremos visualizar dois per�odos, de -T0 at� T0# Definimos o sinal x(t)x = zeros(1, length(t));  # do mesmo tamanho do vetor tempo# adicionamos o temo constantex = a0;N = 5000;      # o número de termos da série de Fourier (voce pode mudar isso a vontade)# Adicionamos os N termosfor k=1:N  x = x + ak(k)*cos(k*w0.*t - k*3*pi/4) + bk(k)*sin(k*w0.*t - k*3*pi/4);endfor# Geramos a figurafigure(1);plot(t, x, 'linewidth', 2);grid on;xlabel('tempo');ylabel('x(t)');l1 = legend('x(t)');set(l1, 'fontsize', 14);title(strcat('x(t) para N=',num2str(N)));set(gca, 'fontsize', 14);# Os gráficos das amplitudes e fasesks = 1:20; # iremos considerar ks termosfigure(2);subplot(2,1,1);stem([0 ks], [c0 ck(ks)], 'linewidth', 2);grid on;xlabel('k');title('Espectro de amplitude');l21 = legend('c_k(k)');set(l21, 'fontsize', 14);set(gca, 'fontsize', 14);subplot(2,1,2);stem([0 ks], [0 thk(ks)], 'linewidth', 2);grid on;xlabel('k');title('Espectro de fase');l22 = legend('theta_k(k)');set(l22, 'fontsize', 14);set(gca, 'fontsize', 14);